sp_widget.css:
table,th{

border: 2px solid black;
}

td,tr{

    border: 1px solid black;
    }


tr:hover{

    background-color: lightskyblue;
}

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */

data.array = [];

var grUser = new GlideRecord('sys_user');
grUser.addEncodedQuery('active',true);
grUser.query();
while(grUser.next()){

var user = {};

user.name = grUser.getValue('user_name');
user.email = grUser.getValue('email');
user.department = grUser.getDisplayValue('department');


data.array.push(user);


}


})();
